写在最前。今天是2021年9月11日，“9·11”事件二十周年。为所有在国际恐怖主义袭击下与在反恐斗争丧生的仁人志士默哀。超级输入输出 #define bufSIZE 1&lt;&lt;15 #ifdef ONLINE_JUDGE #define getchar()(in_p1==in_p2&amp;&amp;(in_p2=(in_p1=in_buf)+fread(in_buf,1,bufSIZE,stdin),in_p1==in_p2)?EOF:*in_p1++) char in_buf[bufSIZE],*in_p1=in_buf,*in_p2=in_buf; #endif template&lt;typename T&gt;inline void redi(T&amp; ret) { ret=0;T f=1;char ch=getchar(); while (ch&lt;&#39;0&#39;||ch&gt;&#39;9&#39;) {if (ch==&#39;-&#39;) f=-f;ch=getchar();} while (ch&gt;=&#39;0&#39;&amp;&amp;ch&lt;=&#39;9&#39;) ret=ret*10+ch-&#39;0&#39;,ch=getchar(); ret*=f; } inline void redi(string&amp; ret) { char ch=getchar(); ret.clear(); while(ch!=EOF&amp;&amp;ch!=&#39;
&#39;&amp;&amp;!isblank(ch))ret.push_back(ch),ch=getchar(); } template &lt;typename T,typename... Args&gt; inline void redi(T&amp; t, Args&amp;... args) { redi(t);redi(args...); } inline void getline(string&amp; ret) { char ch=getchar(); ret.clear(); while(ch!=EOF&amp;&amp;ch!=&#39;
&#39;)ret.push_back(ch),ch=getchar(); } char out_buf[bufSIZE]; int out_p1=-1; const int out_p2=(bufSIZE)-1; inline void flush(){ fwrite(out_buf,1,out_p1+1,stdout),out_p1=-1; } inline void putc(const char &amp;x){ if (out_p1==out_p2)flush(); out_buf[++out_p1]=x; } template&lt;typename T&gt;inline void wrtn(T x){ static char buf[20]; static T len=-1; if (x&gt;=0){ do{ buf[++len]=x%10+48,x/=10; }while(x); } else{ putc(&#39;-&#39;); do{ buf[++len]=-(x%10)+48,x/=10; }while(x); } while(len&gt;=0){ putc(buf[len]),--len; } } inline void wrtn(const string &amp;s){ for(auto ch:s)putc(ch); } inline void wrtn(const char s[]){ wrtn((string)s); } inline void wrtn(char s[]){ wrtn((string)s); } template&lt;typename T&gt;inline void wrti(T x){ wrtn(x),putc(&#39;
